var app = angular.module("SoundcloudApp", ["ngRoute"])

app.factory('soundcloudConnect', [
	function() {
		SC.initialize({
			client_id: 'bda4ada8694db06efcac9cf97b872b3e',
			redirect_uri: '/'
		});
	}
]);


app.config(['$routeProvider','$locationProvider',
	function($routeProvider, $locationProvider) {
    	$routeProvider
			.when("/", {
			    templateUrl : "views/welcome.html"
			})
			.when("/user", {
			    templateUrl : "views/user.html"
			})
			.when("/settings", {
			    templateUrl : "views/settings.html"
			})
			.when("/search", {
			    templateUrl : "views/search.html"
			})
		
		$locationProvider.html5Mode(true);
	}
]);

app.controller('GlobalCtrl', ['$scope', 
	function($scope) {
		$scope.name = 'Matthew';
	}
])

app.controller('UserCtrl', ['$scope', 
	function($scope) {
		$scope.user = {
			name: 'Matthew',
			email: 'mmellows1@gmail.com',
			completion:70,
			color:'#323232',
			genres: ['DnB', 'Dubstep', 'House']
		}
		console.log(SC);
	}
])
